import axios from 'axios';

// Activities API
export const getActivities = async (filters = {}) => {
  try {
    const queryParams = new URLSearchParams();

    // Add filters to query params
    Object.entries(filters).forEach(([key, value]) => {
      if (value !== undefined && value !== null && value !== '') {
        queryParams.append(key, value);
      }
    });

    const res = await axios.get(`/api/activities?${queryParams.toString()}`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const getActivity = async (id) => {
  try {
    const res = await axios.get(`/api/activities/${id}`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const addToFavorites = async (id) => {
  try {
    const res = await axios.post(`/api/activities/${id}/favorite`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const removeFromFavorites = async (id) => {
  try {
    const res = await axios.delete(`/api/activities/${id}/favorite`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const logActivity = async (id, notes) => {
  try {
    const res = await axios.post(`/api/activities/${id}/log`, { notes });
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

// Activity Packs API
export const getActivityPacks = async () => {
  try {
    const res = await axios.get('/api/activities/packs');
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const getPackActivities = async (packId) => {
  try {
    const res = await axios.get(`/api/activities/packs/${packId}`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const purchasePack = async (packId) => {
  try {
    const res = await axios.post(`/api/users/packs/${packId}/purchase`);
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const getPurchasedPacks = async () => {
  try {
    const res = await axios.get('/api/users/packs');
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

// User API
export const getFavorites = async () => {
  try {
    const res = await axios.get('/api/users/favorites');
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};

export const getActivityHistory = async () => {
  try {
    const res = await axios.get('/api/users/history');
    return res.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};
