import React, { createContext, useState, useContext, useEffect } from 'react';
import api, { login, register, getUserProfile, updateUserProfile, updateSubscription, addChild, updateChild, deleteChild } from '../utils/api';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Check if user is logged in on initial load
  useEffect(() => {
    const checkLoggedIn = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          setLoading(false);
          return;
        }

        // Get current user
        const userData = await getUserProfile();

        setUser(userData);
        setIsAuthenticated(true);
        setLoading(false);
      } catch (err) {
        localStorage.removeItem('token');
        setUser(null);
        setIsAuthenticated(false);
        setLoading(false);
      }
    };

    checkLoggedIn();
  }, []);

  // Register user
  const register = async (userData) => {
    try {
      setError(null);
      const res = await api.post('/api/users/register', userData);

      localStorage.setItem('token', res.data.token);

      setUser(res.data.user);
      setIsAuthenticated(true);

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
      throw err;
    }
  };

  // Login user
  const login = async (userData) => {
    try {
      setError(null);
      const res = await api.post('/api/users/login', userData);

      localStorage.setItem('token', res.data.token);

      setUser(res.data.user);
      setIsAuthenticated(true);

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
      throw err;
    }
  };

  // Logout user
  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setIsAuthenticated(false);
  };

  // Update user profile
  const updateProfile = async (userData) => {
    try {
      setError(null);
      const res = await api.put('/api/users/profile', userData);

      setUser({
        ...user,
        name: res.data.user.name,
        email: res.data.user.email
      });

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Profile update failed');
      throw err;
    }
  };

  // Update subscription
  const updateSubscription = async (subscriptionData) => {
    try {
      setError(null);
      const res = await api.put('/api/users/subscription', subscriptionData);

      setUser({
        ...user,
        subscription: res.data.subscription,
        subscriptionExpiry: res.data.expiryDate
      });

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Subscription update failed');
      throw err;
    }
  };

  // Add child profile
  const addChild = async (childData) => {
    try {
      setError(null);
      const res = await api.post('/api/users/children', childData);

      setUser({
        ...user,
        children: [...(user.children || []), res.data.child]
      });

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add child');
      throw err;
    }
  };

  // Update child profile
  const updateChild = async (childId, childData) => {
    try {
      setError(null);
      const res = await api.put(`/api/users/children/${childId}`, childData);

      setUser({
        ...user,
        children: user.children.map(child => 
          child._id === childId ? res.data.child : child
        )
      });

      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update child');
      throw err;
    }
  };

  // Delete child profile
  const deleteChild = async (childId) => {
    try {
      setError(null);
      await api.delete(`/api/users/children/${childId}`);

      setUser({
        ...user,
        children: user.children.filter(child => child._id !== childId)
      });
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete child');
      throw err;
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated,
        loading,
        error,
        register,
        login,
        logout,
        updateProfile,
        updateSubscription,
        addChild,
        updateChild,
        deleteChild
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
